import React from 'react';
import axios from 'axios';

export default class send extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      testURL: 'http://localhost:3888',
      liveURL: 'fakeWebsiteForNow',
      URL: '',
      SessionID: '',
    }
    if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1'){
      this.state.URL = this.state.testURL;
    }else{
      this.state.URL = this.state.liveURL;
    }
  }

  update_auth = () => {
    this.state.SessionID = sessionStorage.getItem('SessionID');
    axios.authorization(this.state.SessionID);
  }

  post = async (route, data) => {
    update_auth();
    var url = this.state.URL + route;
    return new Promise(function(resolve, reject) {
      axios.post(url, data)
      .then(res => {
        resolve(res);
      })
      .catch(err => {
        console.log(err);
        reject(err);
      });
    });
  }

  get = async (route) => {
    update_auth();
    var url = this.state.URL + route;
    return new Promise(function(resolve, reject) {
      axios.get(url)
      .then(res => {
        resolve(res);
      })
      .catch(err => {
        reject(err);
      });
    });
  }

  put = async (route, data) => {
    update_auth();
    var url = this.state.URL + route;
    return new Promise(function(resolve, reject) {
      axios.put(url, data)
      .then(res => {
        resolve(res);
      })
      .catch(err => {
        console.log(err);
        reject(err);
      });
    });
  }
  
}
