import React from 'react';
//import ReactDOM from 'react-dom';
//import axios from 'axios';
import API_URL from './API_URL';
var URL = new API_URL().state.URL;
export default class Login extends React.Component {
  state = {
    username: '',
    password: '',
  }

  onChange = (e) => { 
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  onSubmit = async () => {
    axios.post(URL + '/login', this.state)
      .then(res => {
        console.log(res);
      })
      .catch(err => {
        console.log(err);
      });
  }

  render() {
    return (
      <div>
        <input
          type="text"
          name="username" //The name must match state property
          placeholder="Username"
          onChange={e => this.onChange(e)}
          value={this.state.username} />
        <input
          type="text"
          name="password" //The name must match state property
          placeholder="Password"
          onChange={e => this.onChange(e)}
          value={this.state.password}
          enabled="true" />
        <button onClick={() => this.onSubmit()} type="primary">Submit</button>
      </div>
    );
  }
}
