import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

async function post (url, data) {
  axios.post(url, data)
  .then(res => {
    return res;
  })
  .catch(err => {
    console.log(err);
  });
}

export default function send(route, CRUD, data) {
  //this.post = this.post.bind(this);
  //this.postThrottle = throttle(this.post, 1000);
  var state = {
    testURL: 'http://localhost:3888',
    liveURL: 'fakeWebsiteForNow',
    URL: '',
  }

  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1'){
    state.URL = state.testURL;
  }else{
    state.URL = state.liveURL;
  }
  
switch (CRUD) {
  case 'POST':
    post(state.URL + route, data);
    break;

  default:
    break;
}

