import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

export default function send() {
  //this.post = this.post.bind(this);
  //this.postThrottle = throttle(this.post, 1000);
  this.state = {
    testURL: 'http://localhost:3888',
    liveURL: 'fakeWebsiteForNow',
    URL: '',
  }

  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1'){
    this.state.URL = this.state.testURL;
  }else{
    this.state.URL = this.state.liveURL;
  }
  

  post = async (route, data) => {
    axios.post(this.state.URL + route, data)
    .then(res => {
      return res;
    })
    .catch(err => {
      console.log(err);
    });
  }

  post = async () => {
    axios.post('http://localhost:3888/login', this.state)
    .then(res => {
      console.log(res);
    })
    .catch(err => {
      console.log(err);
    });
  }
}
