import React from 'react';
//import ReactDOM from 'react-dom';
//import axios from 'axios';
import { useAsync } from 'react-async';
import send from './send';
var Send = new send();
export default class Login extends React.Component {
  state = {
    username: '',
    password: '',
  }

  onChange = (e) => { 
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  onSubmit = () => {
    var {res, error, isloading} = useAsync(Send.post('/login', this.state));
    
    console.log(res);
  }

  render() {
    return (
      <div>
        <input
          type="text"
          name="username" //The name must match state property
          placeholder="Username"
          onChange={e => this.onChange(e)}
          value={this.state.username} />
        <input
          type="text"
          name="password" //The name must match state property
          placeholder="Password"
          onChange={e => this.onChange(e)}
          value={this.state.password}
          enabled="true" />
        <button onClick={() => this.onSubmit()} type="primary">Submit</button>
      </div>
    );
  }
}
